            var li = document.createElement('li');
            li.setAttribute("value",opt.value.strip(opt.value.stripTags()));
            li.innerHTML = opt.text.escapeHTML();
            
            if(opt.hasClassName("bold")) {
                li.setStyle('color:#555; font-weight:bold;');
            }

