                                // eslint-disable-next-line no-var
                                var fieldSplit = id.split('|');
                                // eslint-disable-next-line no-var
                                var qid = fieldSplit[0];
                                // eslint-disable-next-line no-var
                                var tempSelector = 'id_' + qid;
