                                const itemArr = parentProductKey.split('_');
                                itemArr[2] = pid;
                                if ($(itemArr.join('_') + '_item_subtotal')) {
                                    $(itemArr.join('_') + '_item_subtotal').update(parseFloat(itemSubtotal).formatMoney(decimal, dSeparator, tSeparator));
                                }
